//Chercher la description extern du item commandé
//Vérifier que le produit exist
public  boolean Match( str itemNumber, str customerAccount)
{
    CustVendExternalItem CustExtItemNum;
    select firstonly CustExtItemNum where CustExtItemNum.ExternalItemId==itemNumber && customerAccount==CustExtItemNum.CustVendRelation;

if(CustExtItemNum!=null)
    {
        itemid=CustExtItemNum.ItemId;
        return true;
    }
    else
    {
        return false;
    }

}
