//chercher l'email du client
public str GetCustomerEmailByAccount(str _custAccount)
{
    CustTable custTable;
    DirPartyTable dirPartyTable;
    str emailAddress;

    // Query the DirPartyContactInfoView to retrieve the email address
    DirPartyContactInfoView dirPartyContactInfoView;

    while select firstOnly Locator
    from dirPartyContactInfoView
    where dirPartyContactInfoView.Party == dirPartyTable.RecId &&
          dirPartyContactInfoView.Type == LogisticsElectronicAddressMethodType::Email
          join RecId from custTable
          where custTable.AccountNum == _custAccount
    {
        emailAddress = dirPartyContactInfoView.Locator;
        break;
    }

    return emailAddress;
}
