/// <summary>
/// vérifier que chaque numéro de commande est différent aux autres
/// </summary>
/// <param name = "worksOrders"></param>
/// <returns>boolean</returns>
public boolean testDuplicateNumber( XmlNodeList worksOrders )
{

    XmlElement apiModel;
    str number ;
    boolean result = true;

    Set worksOrderNumbers = new Set(Types::String); 


    for (int i = 0; i < worksOrders.length() && result; i++)
    {   

        apiModel = worksOrders.item(i);

        number=apiModel.getNamedElement('Worksordernumber').text();

        if(worksOrderNumbers.in(number))
        {
            result=false;

        }
        else 
        {
            worksOrderNumbers.add(number);

        }

    }
    return result;


}
