public void close()
{
    FormDataSource      formDataSource;
    Common              record = element.args().record();

    super();

    if (refreshCaller
        && (record && FormDataUtil::isFormDataSource(record)))
    {
        formDataSource = FormDataUtil::getFormDataSource(record);
        formDataSource.research(true);
    }
}

