public void init()
{
    QueryBuildDataSource  queryDataSourceLink;

    super();

    if (element.args().dataset() == tableNum(SalesTable))
    {
        this.query().dataSourceTable(tableNum(CustPackingSlipJour)).clearDynalinks();

        queryDataSourceLink = this.query().dataSourceTable(tableNum(CustPackingSlipJour)).addDataSource(tableNum(CustPackingSlipSalesLink));
        queryDataSourceLink.relations(true);
        queryDataSourceLink.addDynalink(fieldNum(CustPackingSlipSalesLink, OrigSalesId),
                                        element.args().record(),
                                        fieldNum(SalesTable, SalesId));
    }

    // <GIN>
    if (TaxParameters::checkTaxParameters_IN() &&
        isConfigurationkeyEnabled(configurationKeyNum(LedgerBasic)) &&
        CustParameters::find().MatchedPackingSlipsInquiry &&
        element.args().dataset() == tableNum(CustInvoiceJour))
    {
        this.query().dataSourceTable(tableNum(CustPackingSlipJour)).clearDynalinks();

        queryDataSourceLink = this.query().dataSourceTable(tableNum(CustPackingSlipJour)).addDataSource(tableNum(CustPackingSlipSalesLink));
        queryDataSourceLink.relations(true);
        queryDataSourceLink.addDynalink(fieldNum(CustPackingSlipSalesLink, OrigSalesId),
                                        element.args().record(),
                                        fieldNum(CustInvoiceJour, SalesId));
    }
    // </GIN>

    if (element.args().dataset() == tableNum(CustPackingSlipJour)    &&
        element.args().record().RecId)
    {
        queryDataSourceLink = this.query().dataSourceTable(tableNum(CustPackingSlipJour));
        queryDataSourceLink.addDynalink(fieldNum(CustPackingSlipJour, RecId),
                                        element.args().record(),
                                        fieldNum(CustPackingSlipJour, RecId));
    }
}

